<script>
    function formatNoPol(value) {
        if (!value) return """";

        value = value.toString();

        // Ensure the string is long enough
        if (value.length < 16) return value;

        return value.substring(0, 2) + '.' +
               value.substring(2, 5) + '.' +
               value.substring(5, 7) + '.' +
               value.substring(7, 11) + '.' +
               value.substring(11, 15) + '-' +
               value.substring(15);
    }
</script>");
